package PredatorKorist;

public class Target {

	
	private double x;
	private double y;
	
	public Target( double x, double y ){
		this.x = x;
		this.y = y;
	}
	
	public Target() {
		// TODO Auto-generated constructor stub
	}

	public double getX(){
		return x;
	}
	
	public double getY(){
		return y;
	}
	
	public void setX( double x ){
		this.x = x;
	}
	
	public void setY( double y ){
		this.y = y;
	}
	
}
